//&#65532;

static function all($v){$ret='';
	$u=50000; $n=$u*($v-1); $max=$n+$u;
	for($i=$n;$i<$max;$i++)$ret.=$i.' ';
	return $ret;}

static function call($p){
	$id=val($p,'rid'); $all=val($p,'all'); $ret='';
	$r=explode(' ',self::vars().self::smileys());
	foreach($r as $v)if($v){if(is_numeric($v))$v='#'.$v;
		$ret.=btj('&'.$v.';','insert(\'&'.$v.';\',\''.$id.'\');','btn').' ';}
	return div($ret,'','','width:320px;');}

static function nav($o){
	$ret=aj('asc|ascii|p='.($o-1),pic('previous'));
	$ret.=div($o.' : '.'&#'.$o.';','pane','','font-size:32px;');
	$ret.=aj('asc|ascii|p='.($o+1),pic('next'));
	return div($ret,'','asc');}

static function content($p){
	if($o=val($p,'p'))return self::nav($o);
	$id=val($p,'id'); $all=val($p,'all'); $ret='';
	for($i=1;$i<5;$i++)
	$ret.=aj('popup,,xy|ascii|popwidth:500px,all='.$i.',id='.$id,$i,'btn '.($i==$all?'active':''));
	if($all)$d=self::all($all); else $d=self::vars().self::smileys();
	$r=explode(' ',$d);
	foreach($r as $v){if(is_numeric($v))$v='#'.$v; $ret.=tag('a','title='.$v,'&'.$v.';').' ';}
	$ret=admin_ascii::all();
	return div($ret,'board','ascii');}
